jQuery(function($){
  $(document).ready(function(){
	//модальные окна
		//When you click on a link with class of poplight and the href starts with a # 
$('a.poplight[href^=#]').click(function() {
    var popID = $(this).attr('rel'); //Get Popup Name
    var popURL = $(this).attr('href'); //Get Popup href to define size
 
    //Pull Query & Variables from href URL
    var query= popURL.split('?');
    var dim= query[1].split('&');
    var popWidth = dim[0].split('=')[1]; //Gets the first query string value
 
    //Fade in the Popup and add close button
    $('#' + popID).fadeIn().css({ 'width': Number( popWidth ) }).prepend('<a href="#" class="close" onclick="clozeWind(this)"></a>');
 
    //Define margin for center alignment (vertical   horizontal) - we add 80px to the height/width to accomodate for the padding  and border width defined in the css
    var popMargTop = ($('#' + popID).height() + 80) / 2;
    var popMargLeft = ($('#' + popID).width() + 80) / 2;
 
    //Apply Margin to Popup
    $('#' + popID).css({
        'margin-top' : -popMargTop,
        'margin-left' : -popMargLeft
    });
    //Fade in Background
    $('body').append('<div id="fade"></div>'); //Add the fade layer to bottom of the body tag.
    $('#fade').css({'filter' : 'alpha(opacity=80)'}).fadeIn(); //Fade in the fade layer - .css({'filter' : 'alpha(opacity=80)'}) is used to fix the IE Bug on fading transparencies 
 
    return false;
});


//Close Popups and Fade Layer
	$('a.close, #fade').bind('click', function() { //When clicking on the close or fade layer...
		$('#fade, a.close').remove();
		$('#fade , .popup_block').fadeOut(function() {
			$('#fade, a.close').remove();  //fade them both out
		});
		return false;
	});

	$('a.close_send, #fade').bind('click', function() { //When clicking on the close or fade layer...

			$('#fade, a.close_send').remove();
			$('#fade , .popup_block').fadeOut(function() {
			$('#fade, a.close_send').remove();  //fade them both out
			});
			window.location.reload();
			return false;
	});
	//модальные окна end

  });

});


function clozeWind(elem) {
	jQuery(function($){
		$('#fade, a.close', this).remove();
		$('#fade , .popup_block').fadeOut(function() {
			$('#fade, a.close', elem).remove();  //fade them both out
		});
		//window.location.reload();
		return false;	
	});
}

function clozeWind_reload(elem) {
	jQuery(function($){
		$('#fade, a.close_send', this).remove();
		$('#fade , .popup_block').fadeOut(function() {
			$('#fade, a.close_send', elem).remove();  //fade them both out
		});
		window.location.reload();
		return false;	
	});
}

